import React from 'react'
import axios from 'axios';
import { render } from '@testing-library/react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import './index.css';
import { Button, Row, Col, Container, Form, ListGroup, Group, Label, Control, Text } from 'react-bootstrap';
import Confetti from "react-confetti";
import FigureImage from "react-bootstrap/esm/FigureImage";




export default class TodoList extends React.Component {
    state = {
        todos: [],
        done: "false",
        // checkboxChecked: false
    }


    componentDidMount() {
        axios.get(`http://localhost:5000/todolist`)
            .then(res => {
                const todos = res.data;
                this.setState({ todos });
                console.log(res.data);
            })
    }

    handleChange = event => {
        this.setState({ done: true });
    }


    handleSubmit = event => {
        // event.preventDefault();
        // alert("called");
        axios.patch(`http://localhost:5000/todolist/${this.state.id}`, 
        { 
            done: 'true'
        }, 
        {
            // Config
        }
        )
            .then(res => {
                console.log(res);
                console.log(res.data);
            })
    }

    render() {

        return (
            <ul>
                <Row>
                    <Col xs>
                    </Col>

                    <Col xs>
                        ID:
                        </Col>
                    <Col xs>
                        DESCRITION:
                        </Col>
                    <Col xs>
                        DUE DATE:
                        </Col>
                    <Col xs>
                        PRIORITY:
                        </Col>
                </Row>
                {this.state.todos.map(todolist => <li>
                    <Row>
                        <Col xs>
                            <form onChange={this.handleSubmit}>
                                <input className="checkboxes" type="checkbox" done="done"/>
                            </form>
                        </Col>
                        <Col xs>
                            {todolist.id}
                        </Col>
                        <Col xs>
                            {todolist.description}
                        </Col>
                        <Col xs>
                            {todolist.duedate}
                        </Col>
                        <Col xs>
                            {todolist.priority}
                        </Col>
                    </Row>
                </li>)}
            </ul>
        )
    }
}
