import React from 'react';
import axios from 'axios';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-toastify/dist/ReactToastify.css';
import { Button, Row, Col, Container, Form, ListGroup, Group, Label, Control, Text } from 'react-bootstrap';


export default class ShowSingleItem extends React.Component {
    state = {
        singleItem: [],
        id: '',
    }

    handleChange = event => {
        this.setState({ id: event.target.value });
    }

    handleSubmit = event => {

        axios.get(`http://localhost:5000/todolist/${this.state.id}`)
            .then(res => {
                const singleItem = res.data;
                this.setState({ singleItem });
                console.log(res.data);
            })
    }


    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        <input placeholder="ID" type="text" name="id" onChange={this.handleChange} />
                    </label>
                    <Button variant="primary" type="submit">SHOW </Button>
                    <ul>
                    {this.state.singleItem.map(todolist => <li>
                    <Row>
                        <Col xs>
                            <form onChange={this.handleSubmit}>
                                <input className="checkboxes" type="checkbox" done="done"/>
                            </form>
                        </Col>
                        <Col xs>
                            {singleItem.id}
                        </Col>
                        <Col xs>
                            {singleItem.description}
                        </Col>
                        <Col xs>
                            {singleItem.duedate}
                        </Col>
                        <Col xs>
                            {singleItem.priority}
                        </Col>
                    </Row>
                </li>)}
                    </ul>
                </form>
            </div>
        )
    }
}
