import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Button, Row, Col, Container, Form, ListGroup, Group, Label, Control, Text } from 'react-bootstrap';
import Confetti from "react-confetti";
import FigureImage from "react-bootstrap/esm/FigureImage";
import TodoList from "./TodoList";



function onClickHandler(e) {
  //preventDefault um Prozesse vorher zu beenden
  e.preventDefault();
}

export default function Todo(props) {

  function yeah() {
    alert("checked");
  }

  const [newName, setName] = useState('');

  function handleChange(e) {
    setName(e.target.value);
  }

  const [height, setHeight] = useState(null);
  const [width, setWidth] = useState(null);

  let everythingDone = null;

  function isEverythingDone() {
    if (props.completed == true) {
      everythingDone = <Confetti width={1000} height={1000} />
    } else {
      everythingDone = null;
    }
  }

  const [isEdiging, notEditing] = useState(false);

  function handleSubmit(e) {
    var y = newName;
    if (y == "" || y == " ") {
      // alert("add input");
      props.notifyEmptyInput();
      e.preventDefault();
      notEditing(false);
    } else {
      e.preventDefault();
      props.editTodo(props.id, newName);
      setName("");
      notEditing(false);
    }
  }

  const editingView = (

    <form onSubmit={handleSubmit}>
      <ListGroup.Item>
        <Row>
          <Col>
            <input className="checkboxes" id="todo-0" type="checkbox" defaultChecked={props.completed} onChange={() => props.toggleTaskCompleted(props.id)} />
          </Col>
          <Col>
            {/* <Form.Group controlID="Text01"> */}
            {/* <Form.Control type="input"></Form.Control> */}
            {/* </Form.Group> */}
            <input id={props.id} type="text" value={newName} onChange={handleChange} />
          </Col>
          <Col>
            <Form.Group controlID="Check02">
              {/* <Button variant="success">DONE</Button> */}
              <Button onClick={onClickHandler} variant="success" onClick={handleSubmit}>SAVE</Button>
              <Button onClick={onClickHandler} variant="danger" onClick={() => notEditing(false)}>CANCEL</Button>
            </Form.Group>
          </Col>
        </Row>
      </ListGroup.Item>
    </form>

  )

  const normalView = (

    <ListGroup.Item>
      <Row>
        {props.everythingDone}
        <Col>
          <input className="checkboxes" id="todo-0" type="checkbox" defaultChecked={props.completed} onChange={() => props.toggleTaskCompleted(props.id)} />
        </Col>
        <Col>
          {/* <Form.Group controlID="Text01"> */}
          {/* <Form.Control type="input"></Form.Control> */}
          {/* </Form.Group> */}
          <TodoList/>
          {/* {props.name} */}
        </Col>
        <Col>
          <Form.Group controlID="Check02">
            {/* <Button variant="success">DONE</Button> */}
            <Button onClick={onClickHandler} variant="primary" onClick={() => notEditing(true)}>EDIT</Button>
            <Button onClick={onClickHandler} variant="danger" onClick={() => props.deleteTodo(props.id)}>DELETE</Button>
          </Form.Group>
        </Col>
      </Row>
    </ListGroup.Item>

  )


  return (
    <li>{isEdiging ? editingView : normalView}</li>
  )
}
