import React, { useState } from "react";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import FilterButtons from './FilterButtons';
import { Button, Row, Col, Form, ListGroup } from 'react-bootstrap';



export default function Input(props) {

    const [name, setName] = useState(' ');

    function somethingChanged(e) {
        setName(e.target.value);
    }

    //Callback Hooks
    //props.addTask("blabla"); an funkton an addTask geben in App
    function handleSubmit(e) {
        var x = name;
        console.log(x);
        if (x == "" || x == " ") {
            e.preventDefault();
            props.notifyEmptyInput();
        } else {
            e.preventDefault();
            props.addTask(name);
            setName("");
        }
    }

    return (
        <form onSubmit={handleSubmit}>
            <ListGroup.Item>
                <Row>
                    <Col>
                        <div>
                            {/* Input Feld oben */}
                            <Form.Control id="addBtn" type="input" value={name} onChange={somethingChanged} />
                        </div>
                    </Col>
                    <Col xs lg="2">
                        <Button variant="success" type="submit">ADD</Button>
                    </Col>
                </Row>
            </ListGroup.Item>
        </form>
    );
}
