import React, { useState, useRef, useEffect } from "react";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Row, Col, Container, Form, ListGroup, Group, Label, Control, Text } from 'react-bootstrap';


export default function FilterButtons(props) {


    function setActive(e) {
        e.preventDefault();
        props.setActive('NotCompleted');
    }

    function setAll(e) {
        e.preventDefault();
        props.setAll('All');
    }

    function setDone(e) {
        e.preventDefault();
        props.setAll('completed');
    }

    return (
        <div>
            <Form.Group controlID="FilterButtons">
                {/* UNCHECKED ENTRIES */}
                {/* <button type="button" className="btn toggle-btn" aria-pressed={props.isPressed} onClick={() => props.setFilter(props.name)}> */}
                <Button aria variant="dark" aria-pressed={props.isPressed} onClick={setActive}>GET SHIT DONE üí©</Button>
                {/* CHECKED ENTRIES */}
                <Button variant="dark" onClick={setDone}>DONE üí™</Button>
                {/* ALL ENTRIES */}
                <Button variant="dark" onClick={setAll}>ARCHIVE üóÑÔ∏è</Button>
            </Form.Group>
        </div>
    );
}
