import React from 'react';
import axios from 'axios';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Row, Col, Form, ListGroup } from 'react-bootstrap';

export default class AddTask extends React.Component{
    state = {
        description: '',
        duedate: '',
        priority: 'high'
      }

      handleChange = event => {
        this.setState({ description: event.target.value });
      }

      handleChange2 = event => {
        this.setState({ duedate: event.target.value });
      }

      handleChange3 = event => {
        this.setState({ priority: event.target.value });
      }


      handleSubmit = event => {
        // event.preventDefault();

        axios.post(`http://localhost:5000/todolist`, {
          description: this.state.description,
          duedate: this.state.duedate,
          done: "false",
          priority: this.state.priority
        })
          .then(res => {
            console.log(res);
            console.log(res.data);
          })
      }

      render() {
        return (
          <div>
            <form onSubmit={this.handleSubmit}>
              <label>
                Add TODO:
                <input type="text" description="description" onChange={this.handleChange} />
                <input type="date" duedate="duedate" onChange={this.handleChange2} />
                <select priority="priority" onChange={this.handleChange3}>
                  <option value="high">high</option>
                  <option value="medium">medium</option>
                  <option value="low">low</option>
                </select>
              </label>
              <button type="submit">Add</button>
            </form>
          </div>
        )
      }
}
