import React from 'react'
import axios from 'axios';
import { render } from '@testing-library/react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import './index.css';
import { Button, Row, Col, Container, Form, ListGroup, Group, Label, Control, Text } from 'react-bootstrap';
import Confetti from "react-confetti";
import FigureImage from "react-bootstrap/esm/FigureImage";




export default class TodoList extends React.Component {
    state = {
        todos: []
    }


    componentDidMount() {
        axios.get(`http://localhost:5000/todolist`)
            .then(res => {
                const todos = res.data;
                this.setState({ todos });
                console.log(res.data);
            })
    }

    render() {

        return (
            <ul>
                {this.state.todos.map(todolist => <li>
                    <Row>
                        <Col xs>
                            <input className="checkboxes" type="checkbox" done="done" onChange={this.handleChange} />
                        </Col>
                        <Col xs>
                            <p>id:</p> {todolist.id}
                        </Col>
                        <Col xs>
                            <p>description: </p>
                            {todolist.description}
                        </Col>
                        <Col xs>
                            due-date: {todolist.duedate}
                        </Col>
                        <Col xs>
                            priority: {todolist.priority}
                        </Col>
                    </Row>
                </li>)}
            </ul>
        )
    }
}
